<script lang="ts">
  import { getContext } from 'svelte';

  import { hideOnSSR, portal } from '../../actions/portal';
  import { useStore } from '../../store';
  import type { EdgeLabelProps } from './types';
  import { toPxString } from '../../utils';

  let {
    x = 0,
    y = 0,
    width,
    height,
    selectEdgeOnClick = false,
    transparent = false,
    class: className,
    children,
    ...rest
  }: EdgeLabelProps = $props();

  const store = useStore();
  const edgeId = getContext<string>('svelteflow__edge_id');

  if (!edgeId) {
    throw new Error('EdgeLabel must be used within an edge');
  }

  let z = $derived(store.visible.edges.get(edgeId)?.zIndex);
</script>

<div
  use:portal={'edge-labels'}
  style:display={hideOnSSR().value ? 'none' : undefined}
  class={['svelte-flow__edge-label', { transparent }, className]}
  style:cursor={selectEdgeOnClick ? 'pointer' : undefined}
  style:transform="translate(-50%, -50%) translate({x}px,{y}px)"
  style:pointer-events="all"
  style:width={toPxString(width)}
  style:height={toPxString(height)}
  style:z-index={z}
  tabindex="-1"
  onclick={() => {
    if (selectEdgeOnClick) store.handleEdgeSelection(edgeId);
  }}
  {...rest}
>
  {@render children?.()}
</div>

<style>
  .transparent {
    background: transparent;
  }
</style>
